using System;using System.Collections.Generic;using System.Linq;using System.Web;using System.Web.UI;using System.Web.UI.WebControls;namespace Cshop1._1{    public partial class Formulario_web1 : System.Web.UI.Page    {        protected void Page_Load(object sender, EventArgs e)        {            if (!IsPostBack) {             DropDownList1.Items.Add("Consulta General");            DropDownList1.Items.Add("Pedido");            DropDownList1.Items.Add("Informe de un problema");            }        }        protected void Button2_Click(object sender, EventArgs e)        {                       AccesoDatos aq = new AccesoDatos();            string tipo = DropDownList1.SelectedItem.Value;            aq.aplicarconsultasql("insert into [MensajeContacto] values ('" + Nombre.Text + "','" + mail.Text + "','"+tipo+"','" + Mensaje.Text + "')");            mail.Text = "";            Nombre.Text = "";            Mensaje.Text = "";
            ClientScript.RegisterStartupScript(this.GetType(), "alert", "alert('Se ha enviado su consulta al administrador')", true);        }    }}